mapper.update:
UPDATE dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
WHERE id = #{id}

mapper.select:
select dish.id, dish.name, dish.category_id, dish.price, dish.image, dish.description, dish.status, dish.update_time, category.name as category_name
from dish left join category on dish.category_id = category.id


        and dish.name like concat('%',#{name},'%')


        and dish.category_id = #{categoryId}


        and dish.status = #{status}



order by dish.category_id asc , dish.status asc
