mapper.update:
update orders set status = #{status}


        and id = #{orders.id}


        and number = #{orders.number}


        and status = #{orders.status}


        and user_id = #{orders.userId}


        and address_bookId = #{orders.addressBookId}


        and order_time < #{orders.orderTime}


        and checkout_time = #{orders.checkoutTime}


        and pay_method = #{orders.payMethod}


        and pay_status = #{orders.payStatus}


        and amount = #{orders.amount}


        and remark = #{orders.remark}


        and user_name = #{orders.userName}


        and phone = #{orders.phone}


        and address = #{orders.address}


        and consignee = #{orders.consignee}


        and rejection_reason = #{orders.rejectionReason}


        and cancel_time = #{orders.cancelTime}


        and estimated_delivery_time = #{orders.estimatedDeliveryTime}


        and delivery_status = #{orders.deliveryStatus}


        and delivery_time = #{orders.deliveryTime}


        and pack_amount = #{orders.packAmount}


        and tableware_number = #{orders.tablewareNumber}


        and tableware_status = #{orders.tablewareStatus}

mapper.select:
select * from orders


        and id = #{id}


        and number like concat('%',#{number},'%')


        and status = #{status}


        and user_id = #{userId}


        and address_bookId = #{addressBookId}


        and order_time = #{orderTime}


        and checkout_time = #{checkoutTime}


        and pay_method = #{payMethod}


        and pay_status = #{payStatus}


        and amount = #{amount}


        and remark = #{remark}


        and user_name = #{userName}


        and phone like concat('%',#{phone},'%')


        and address = #{address}


        and consignee = #{consignee}


        and rejection_reason = #{rejectionReason}


        and cancel_time = #{cancelTime}


        and estimated_delivery_time = #{estimatedDeliveryTime}


        and delivery_status = #{deliveryStatus}


        and delivery_time = #{deliveryTime}


        and pack_amount = #{packAmount}


        and tableware_number = #{tablewareNumber}


        and tableware_status = #{tablewareStatus}

select count(*) from orders


        and id = #{orders.id}


        and number = #{orders.number}


        and status = #{orders.status}


        and user_id = #{orders.userId}


        and address_bookId = #{orders.addressBookId}


