mapper.update:
update shopping_cart
name = #{name}, user_id = #{userId}, dish_id = #{dishId}, setmeal_id = #{setmealId}, dish_flavor = #{dishFlavor}, number = #{number}, amount = #{amount}, image = #{image}, create_time = #{createTime},
where id = #{id}

mapper.select:
select * from shopping_cart


        and dish_id = #{shoppingCartDTO.dishId}


        and setmeal_id = #{shoppingCartDTO.setmealId}


        and dish_flavor = #{shoppingCartDTO.dishFlavor}

    and user_id = #{uid}
